import React from 'react';
import BoatModelViewer from './BoatModelViewer';
import './MainContent.css';
import GrandSturdy from '../img/GrandSturdy.png';

function MainContent() {
  return (
    <div className="main-content">
      {/* Left Panel */}
      <div className="left-panel">
        {/* First Division: Boat Info, Image, and 3D Viewer */}
        <div className="info-3d-container">
          {/* Boat Info on the right */}
          <div className="boat-info">
            <h2>Grand Sturdy 30.0 AC</h2>
            <p>9.70 x 3.35 x 1.00 m</p>
            <p>4 (+2) beds</p>
            <p>December 2024</p>
          </div>

          {/* Boat Image on the left */}
          <div className="boat-image">
            <img src={GrandSturdy} alt="Grand Sturdy Boat" />
          </div>
        </div>

        {/* 3D Viewer Placeholder Below Info & Image */}
        <div className="three-d-visualization">
          {/* Placeholder for 3D Viewer */}
          {/* Uncomment and add <BoatModelViewer /> component */}
          {/* <BoatModelViewer /> */}
        </div>

        {/* Second Division: Report Download */}
        <div className="pdf-report">
          <h3>Download Report</h3>
          <a href="src/reports/Report_GS30AC_12-2024.pdf" download>Download Report</a>
        </div>
      </div>

      {/* Right Panel */}
      <div className="right-panel">
        {/* Darker Grey Rectangle */}
        <div className="right-panel-placeholder">
          {/* Placeholder for future content */}
        </div>
      </div>
    </div>
  );
}

export default MainContent;
